-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('apbgf_update_staff_data', 'apbgf_wizard_completed', 'apbgf_settings', 'apbgf_email_templates', 'active_sitewide_plugins', 'apbgf_reports');
DELETE FROM wp_options WHERE option_name LIKE 'apbgf_form_settings_%';

