$transients = $wpdb->get_col( $wpdb->prepare( "SELECT option_name FROM {$wpdb->options} WHERE option_name LIKE %s OR option_name LIKE %s", '_transient_asm_cart_data_%', '_site_transient_asm_cart_data_%' ) );
