-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('wpfb_enable_fbbot', 'wpfb_verify_token', 'wpfb_app_id', 'wpfb_app_secret', 'wpfb_user_access_token', 'wpfb_page_access_token', 'wpfb_default_instruction', 'wpfb_default_no_match', 'wpfb_command_live_agent', 'wpfb_contact_admin_text', 'wp_chatbot_df_api', 'support_query', 'support_ans', 'qlcd_wp_chatbot_sys_key_support', 'qlcd_wp_site_search', 'qlcd_wp_chatbot_support_phone', 'qlcd_wp_chatbot_support_email', 'qlcd_wp_chatbot_dialogflow_agent_language', 'qlcd_wp_chatbot_dialogflow_client_token', 'qlcd_wp_chatbot_dialogflow_defualt_reply', 'qlcd_wp_chatbot_wildcard_msg', 'qlcd_wp_send_us_email', 'qlcd_wp_chatbot_asking_email', 'qlcd_wp_chatbot_search_keyword', 'qlcd_woo_chatbot_asking_email', 'qlcd_wp_chatbot_asking_msg', 'qlcd_wp_chatbot_admin_email', 'qlcd_wp_chatbot_email_fail', 'qlcd_wp_chatbot_email_sent', 'qlcd_wp_chatbot_asking_phone', 'qlcd_woo_chatbot_asking_phone', 'qlcd_wp_chatbot_phone_sent', 'qlcd_woo_chatbot_phone_sent', 'qlcd_wp_email_subscription_success', 'qlcd_wp_email_already_subscribe', 'wpwl_word_wpbot', 'wpwo_word_wpbot', 'qc_get_started_new', 'qlcd_wp_chatbot_dialogflow_project_id', 'qlcd_wp_chatbot_dialogflow_project_key');
DELETE FROM wp_options WHERE option_name LIKE 'bot_%';
DELETE FROM wp_options WHERE option_name LIKE '%_feedback';
DELETE FROM wp_options WHERE option_name LIKE '%_feedback_email';
DELETE FROM wp_options WHERE option_name LIKE '%_sitesearch';
DELETE FROM wp_options WHERE option_name LIKE '%_subscription';
DELETE FROM wp_options WHERE option_name LIKE '%_feedback_msg';
DELETE FROM wp_options WHERE option_name LIKE '%_phone';

