-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('convertybot_options', 'convertybot_version', 'convertybot_chatbot_version', 'convertybot_cookie_policy_url', 'convertybot_coupon_notify_queue', 'convertybot_last_sync', 'convertybot_backend_url', 'convertybot_gdpr_enabled', 'convertybot_ccpa_enabled', 'convertybot_respect_dnt', 'convertybot_cookieless', 'convertybot_respect_consent', 'convertybot_anonymize_ip', 'convertybot_privacy_policy_version', 'convertybot_site_id');

-- Delete Metadata
DELETE FROM wp_postmeta WHERE meta_key IN ('_wp_attachment_image_alt', 'rating', 'thumbnail_id', 'first_name', 'last_name', 'convertybot_consent', 'convertybot_consent_details', 'convertybot_consent_timestamp', 'convertybot_functional_consent', 'convertybot_analytics_consent', 'convertybot_marketing_consent', 'convertybot_personalization_consent', 'convertybot_advertising_consent', 'verified', '_convertybot_guest_migrated', '_convertybot_migration_date');
