global $wpdb;
$options = $wpdb->get_col( $wpdb->prepare( "SELECT option_name FROM {$wpdb->options} WHERE option_name LIKE %s", 'gsf_preset_options_keys_%' ) );
foreach ( $options as $opt ) {
	delete_option( $opt );
