-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('woocommerce_thanks_page_id', 'woocommerce_cart_page_id', 'woocommerce_price_decimal_sep', 'woocommerce_price_thousand_sep', 'woo_freepay_version', 'woo_freepay_settings', 'woo_freepay_maintenance', '_wcfp_admin_notices', '_wcfp_admin_runtime_errors');
DELETE FROM wp_options WHERE option_name LIKE 'wcfp_transaction_%';

