<?php

// If uninstall not called from WordPress, then exit.
if ( ! defined( 'WP_UNINSTALL_PLUGIN' ) ) {
	exit;
}

// Delete Options
delete_option('idcrm_settings');
delete_site_option('idcrm_settings');
global $wpdb;
$options = $wpdb->get_col( $wpdb->prepare( "SELECT option_name FROM {$wpdb->options} WHERE option_name LIKE %s", '%_expire' ) );
foreach ( $options as $opt ) {
	delete_option( $opt );
	delete_site_option( $opt );
}
delete_option('idcrm_mailbox');
delete_site_option('idcrm_mailbox');
delete_option('idcrm_zadarma_unknown_calls');
delete_site_option('idcrm_zadarma_unknown_calls');

// Delete Transients
delete_transient('update_plugins');
delete_site_transient('update_plugins');

