-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('logichop_trial', 'logichop-settings', 'logichop_instance', 'logichop_activated', 'logichop_local_storage', 'logichop_version', 'logichop');
DELETE FROM wp_options WHERE option_name LIKE 'logic-hop-dismiss-notice-%';
DELETE FROM wp_options WHERE option_name LIKE 'logichop_recipe_import_%';
DELETE FROM wp_options WHERE option_name LIKE 'logichop_recipe_import_result_%';
DELETE FROM wp_options WHERE option_name LIKE '%active';

