-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('mstw-lmao-settings', 'mstw_fw_settings', 'lm-update-games-sport', 'lm-current-sport', 'lm-current-league', 'lm-update-games-date', 'mstw_lm_venue_options', 'mstw_league_manager_activated');
DELETE FROM wp_options WHERE option_name LIKE 'lm-league-seasons_%';
DELETE FROM wp_options WHERE option_name LIKE 'lm-sport-options_%';

