-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('peerraiser_options', 'peerraiser_slug', 'sidebars_widgets', 'peerraiser_activity_feed', 'peerraiser_options_updated', 'peerraiser_donations_total');
DELETE FROM wp_options WHERE option_name LIKE 'widget_%';
DELETE FROM wp_options WHERE option_name LIKE '%_db_version';

