-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('pv_custom_email_templates', 'ndpv_payment_wc', 'ndpv_package_payment', 'ndpv_white_label', 'ndpv_smtp', 'ndpv_email_footer', 'ninja_forms_version', 'ndpv_active', 'ndpv_version', 'active_sitewide_plugins', 'ndpvp_version', 'ndpv_workspace_default', 'ndpv_version_install', 'ndpv_subscription');
DELETE FROM wp_options WHERE option_name LIKE '%_general';
DELETE FROM wp_options WHERE option_name LIKE 'ndpv_%';
DELETE FROM wp_options WHERE option_name LIKE '%_total';
DELETE FROM wp_options WHERE option_name LIKE '%_password';

