-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('reputando_locations', 'reputando_token', 'reputando_selected_location', 'reputando_user_id', 'reputando_plugin_activated', 'reputando_badge_layout_error');

