-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('sfrdcd_debug_mode', 'sfrdcd_bd_show_default_image', 'sfrdcd_bd_default_category_image', 'usfrdcd_directorist_enabled', 'usfrdcd_universal_enabled', 'usfrdcd_title_format', 'usfrdcd_show_image', 'usfrdcd_image_size', 'usfrdcd_show_subcategories', 'sfrdcd_show_category_icons', 'usfrdcd_subcategories_title', 'usfrdcd_show_subcategory_descriptions', 'usfrdcd_inject_position', 'sfrdcd_show_description', 'sfrdcd_color_mode', 'sfrdcd_primary_color', 'sfrdcd_background_color', 'sfrdcd_text_color', 'sfrdcd_border_color', 'sfrdcd_subcategory_primary_color', 'sfrdcd_subcategory_hover_color', 'sfrdcd_title_format', 'sfrdcd_image_size', 'sfrdcd_selected_directory_plugin', 'sfrdcd_inject_content', 'sfrdcd_show_image', 'sfrdcd_inject_method', 'sfrdcd_image_alignment', 'sfrdcd_tag_show_title', 'sfrdcd_show_title', 'sfrdcd_custom_class', 'sfrdcd_style_type', 'sfrdcd_image_position', 'sfrdcd_title_tag', 'sfrdcd_show_subcategories', 'sfrdcd_subcategories_layout', 'sfrdcd_subcategories_title', 'sfrdcd_subcategories_heading_tag', 'sfrdcd_max_subcategories', 'sfrdcd_show_subcategories_title', 'sfrdcd_inject_position', 'sfrdcd_show_icon', 'sfrdcd_custom_image_size', 'sfrdcd_show_subcategory_descriptions', 'sfrdcd_subcategory_animation', 'sfrdcd_subcategory_show_image', 'sfrdcd_subcategory_show_title', 'sfrdcd_subcategory_title_format', 'sfrdcd_activation_date', 'sfrdcd_licence_status');
DELETE FROM wp_options WHERE option_name IN ('sfrdcd_development_mode', 'sfrdcd_uninstall_data');

-- Delete Metadata
DELETE FROM wp_postmeta WHERE meta_key IN ('_rtcl_image', '_rtcl_icon', 'image', 'category_icon', 'sfrdcd_pro_notice_dismissed_permanent', 'sfrdcd_pro_notice_dismissed_until', 'wpbdp_image', 'wpbdp_icon', 'rank_math_description', 'yoast_wpseo_metadesc');
DELETE FROM wp_usermeta WHERE meta_key IN ('_rtcl_image', '_rtcl_icon', 'image', 'category_icon', 'sfrdcd_pro_notice_dismissed_permanent', 'sfrdcd_pro_notice_dismissed_until', 'wpbdp_image', 'wpbdp_icon', 'rank_math_description', 'yoast_wpseo_metadesc');
DELETE FROM wp_termmeta WHERE meta_key IN ('_rtcl_image', '_rtcl_icon', 'image', 'category_icon', 'sfrdcd_pro_notice_dismissed_permanent', 'sfrdcd_pro_notice_dismissed_until', 'wpbdp_image', 'wpbdp_icon', 'rank_math_description', 'yoast_wpseo_metadesc');
DELETE FROM wp_commentmeta WHERE meta_key IN ('_rtcl_image', '_rtcl_icon', 'image', 'category_icon', 'sfrdcd_pro_notice_dismissed_permanent', 'sfrdcd_pro_notice_dismissed_until', 'wpbdp_image', 'wpbdp_icon', 'rank_math_description', 'yoast_wpseo_metadesc');

