DELETE FROM wp_options WHERE option_name IN ('SIRV_CUSTOM_CSS', 'SIRV_EXCLUDE_FILES', 'SIRV_EXCLUDE_PAGES', 'SIRV_WP_MEDIA_LIBRARY_SIZE', 'SIRV_HTTP_AUTH_CHECK', 'SIRV_HTTP_AUTH_USER', 'SIRV_HTTP_AUTH_PASS', 'SIRV_SYNC_ON_UPLOAD', 'SIRV_PREVENT_CREATE_WP_THUMBS', 'SIRV_THUMBS_DATA', 'SIRV_DELETE_FILE_ON_SIRV', 'SIRV_CSS_BACKGROUND_IMAGES_SYNC_DATA', 'SIRV_CSS_BACKGROUND_IMAGES', 'SIRV_WOO_MV_CONTAINER_CUSTOM_CSS', 'SIRV_WOO_CONTAINER_CLASSES', 'SIRV_WP_NETWORK_WIDE', 'SIRV_VERSION_PLUGIN_INSTALLED', 'SIRV_MUTE', 'SIRV_STAT', 'SIRV_FOLDERS_DATA', 'WP_USE_SIRV_CDN', 'WP_SIRV_SHORTCODES_PROFILES', 'WP_SIRV_CDN_PROFILES', 'WP_USE_SIRV_RESPONSIVE', 'WP_SIRV_JS', 'WP_FOLDER_ON_SIRV', 'SIRV_MUTE_ERROR_MESSAGE', 'SIRV_AWS_BUCKET', 'SIRV_NETWORK_TYPE', 'sirv_admin_notices', 'sirv_review_notice', 'SIRV_WOO_MV_CUSTOM_OPTIONS', 'isSirvActivated');

-- Delete Metadata
DELETE FROM wp_postmeta WHERE meta_key IN ('_thumbnail_id', 'fifu_image_url', 'sirv_woo_product_image_attachment', '_wp_attachment_image_alt');
DELETE FROM wp_usermeta WHERE meta_key IN ('_thumbnail_id', 'fifu_image_url', 'sirv_woo_product_image_attachment', '_wp_attachment_image_alt');
DELETE FROM wp_termmeta WHERE meta_key IN ('_thumbnail_id', 'fifu_image_url', 'sirv_woo_product_image_attachment', '_wp_attachment_image_alt');
DELETE FROM wp_commentmeta WHERE meta_key IN ('_thumbnail_id', 'fifu_image_url', 'sirv_woo_product_image_attachment', '_wp_attachment_image_alt');

