-- WordPress Plugin Uninstall SQL Script

-- Delete Options & Transients
DELETE FROM wp_options WHERE option_name IN ('triggervox_plugin_status', 'triggervox_current_userid', 'triggervox_setup_completed', 'triggervox_wpforms_integration_enabled', 'triggervox_cf7_integration_enabled', 'triggervox_unique_id', 'triggervox_integration_mode', 'triggervox_setup_banner_dismissed', 'triggervox_phone_verified', 'triggervox_last_phone_check', 'triggervox_button_bg_color', 'triggervox_button_text_color', 'triggervox_button_text', 'triggervox_button_photo', 'triggervox_popup_bg_color', 'triggervox_button_color', 'triggervox_text_color', 'triggervox_display_type', 'triggervox_widget_delay', 'triggervox_form_header', 'triggervox_form_subtitle', 'triggervox_form_disclaimer', 'triggervox_submit_button_text', 'triggervox_form_admin_only', 'triggervox_form_enable_debug_mode', 'triggervox_display_rules_type', 'triggervox_display_rules_pages', 'triggervox_display_rules_excluded_pages', 'triggervox_cf7_form_ids', 'triggervox_version', 'triggervox_last_subscription_check', 'triggervox_manual_urls', 'triggervox_excluded_urls', 'triggervox_debug_mode', 'triggervox_user_type', 'triggervox_subscription_status', 'triggervox_company_data', 'triggervox_wpforms_form_ids', 'triggervox_wpforms_add_to_contacts', 'triggervox_webhook_secret', 'triggervox_nextjs_app_url', 'triggervox_public_key', 'triggervox_assistant_id', 'triggervox_vapi_key', 'ai_call_agent_public_key', 'ai_call_agent_assistant_id', 'ai_call_agent_vapi_key', 'triggervox_webhook_urls_persistent', 'triggervox_api_keys_persistent', 'triggervox_setup_completed_at');
DELETE FROM wp_options WHERE option_name IN ('triggervox_script_id', 'triggervox_all_settings_cache', 'triggervox_analytics_all_periods_summary', 'triggervox_setup_status', 'triggervox_user_config_3', 'triggervox_failed_logs', 'triggervox_upgrade_logged_version', 'triggervox_webhook_urls', 'triggervox_api_keys', 'triggervox_subscription_status', 'triggervox_api_connection_test', 'triggervox_analytics_data');
DELETE FROM wp_options WHERE option_name LIKE 'triggervox_analytics_summary_%';
DELETE FROM wp_options WHERE option_name LIKE 'triggervox_user_config_%';

-- Delete Metadata
DELETE FROM wp_postmeta WHERE meta_key IN ('triggervox_wpforms_notice_dismissed', 'triggervox_cf7_notice_dismissed', 'triggervox_plugin_enabled_notice_dismissed', 'triggervox_setup_notice_dismissed');
DELETE FROM wp_usermeta WHERE meta_key IN ('triggervox_wpforms_notice_dismissed', 'triggervox_cf7_notice_dismissed', 'triggervox_plugin_enabled_notice_dismissed', 'triggervox_setup_notice_dismissed');
DELETE FROM wp_termmeta WHERE meta_key IN ('triggervox_wpforms_notice_dismissed', 'triggervox_cf7_notice_dismissed', 'triggervox_plugin_enabled_notice_dismissed', 'triggervox_setup_notice_dismissed');
DELETE FROM wp_commentmeta WHERE meta_key IN ('triggervox_wpforms_notice_dismissed', 'triggervox_cf7_notice_dismissed', 'triggervox_plugin_enabled_notice_dismissed', 'triggervox_setup_notice_dismissed');

